custom-sql.sql:
SELECT opencps_dossier_log.* FROM opencps_dossier_log WHERE opencps_dossier_log.dossierId = ? AND ( (opencps_dossier_log.actor = 1 OR opencps_dossier_log.actor = 2) AND opencps_dossier_log.actionInfo NOT IN("add-attachment-file","remove-attachment-file","clone-attachment-file")
)
ORDER BY modifiedDate DESC
